import React, {useState, useEffect} from "react";
import {NavLink, Routes, Route, useParams} from "react-router-dom";
import ProductDetailInfo from "./ProductDetailInfo.js";
import ProductDetailNutrition from "./ProductDetailNutrition.js";
import ProductDetailStorage from "./ProductDetailStorage.js";
import useFetch from "./useFetch.js";

export default function ProductDetails(props) {
  const [product, setProduct] = useState([]);
  const {get, loading} = useFetch("https://react-tutorial-demo.firebaseio.com/");
  const params = useParams();
  console.log(params);
  useEffect(() => {
    get(`productinfo/id${params.id}.json`).then(data => {
      console.log(data)
      setProduct(data)
    })
  }, [])

  return <><div className="product-details-layout">
  <div>
    <h2>{product.name}</h2>
    <img src={product.image} width="125" height="125" className="product-details-image" alt={product.name}
    />
  </div>
  <div>
    <div className="tabs">
      <ul>
        <li>
          <NavLink exact to={`/products/${product.id}`}>Details</NavLink>
        </li>
        <li>
          <NavLink exact to={`/products/${product.id}/nutrition`}>Nutrition</NavLink>
        </li>
        <li>
          <NavLink exact to={`/products/${product.id}/storage`}>Storage</NavLink>
        </li>
      </ul>
    </div>
  </div>
  <Routes>
    <Route exact path={`/products/${product.id}`} element={<ProductDetailInfo price={product.price} description={product.description}/>} />
    <Route exact path={`/products/${product.id}/nutrition`} element={<ProductDetailNutrition {...product.nutrition}/>} />
    <Route exact path={`/products/${product.id}/storage`} element={<ProductDetailStorage storage={product.storage}/>} />
  </Routes>
</div></>
}
